'use strict';


class Spead {
    constructor() {
        this.i = 0;
    }

    record() {
        const value = process.hrtime.bigint();
        
        let result;
        if(this.value !== undefined) result = value - this.value;
        else {
            result = 0;
            this.value = value;
        }

        return result;
    }

    log() {
        console.log('record', this.i,':', this.record());
        this.i++;
    }
}


module.exports = Spead;
