'use strict';


class Timer {
    constructor(interval) {
        this.interval = interval;
        this.duration = 0;
    }
		
	count(pressed) {
        pressed === undefined ? pressed = true : {};
        if (this.timePassed() > this.interval && pressed) {
			this.duration = this.time();
			return true;
		}
		else {
			return false;
        }
    }

    time() {
        const date = new Date();
        return date.getTime();
    }
    
    timePassed() {
        return this.time() - this.duration;
    }
}


module.exports = Timer;
